import { DestinationGrid } from '@/components/DestinationGrid';
import { TravelGuide } from '@/components/TravelGuide';
import { RouteMap } from '@/components/RouteMap';
import { Snowfall } from '@/components/Snowfall';

const Index = () => {
  const [activeTab, setActiveTab] = useState('explore');

  return (
    <div className="min-h-screen bg-gradient-to-b from-winter-sky to-background relative overflow-hidden">
      <Snowfall />

      <Hero />

      <div className="container mx-auto px-4 py-12">
        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full max-w-2xl mx-auto grid-cols-3 mb-12">
            <TabsTrigger value="explore">探索景点</TabsTrigger>
            <TabsTrigger value="guide">路书指导</TabsTrigger>
            <TabsTrigger value="route">行程规划</TabsTrigger>
          </TabsList>

          <TabsContent value="explore" className="mt-0">
            <DestinationGrid />
          </TabsContent>

          <TabsContent value="guide" className="mt-0">
            <TravelGuide />
          </TabsContent>

          <TabsContent value="route" className="mt-0">
            <RouteMap />
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
};

export default Index;

import { Snowflake } from 'lucide-react';
import { motion } from 'framer-motion';

export const Hero = () => {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      className="relative pt-24 pb-16 text-center"
    >
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ delay: 0.3, type: "spring", stiffness: 200 }}
          className="inline-flex items-center justify-center w-20 h-20 bg-primary/10 rounded-full mb-6"
        >
          <Snowflake className="w-12 h-12 text-primary" />
        </motion.div>

        <h1 className="text-5xl md:text-7xl font-bold mb-6 bg-gradient-to-r from-primary via-accent to-primary bg-clip-text text-transparent">
          冰雪奇缘之旅
        </h1>

        <p className="text-xl md:text-2xl text-muted-foreground max-w-3xl mx-auto mb-8">
          探索冰雪世界的绝美风光，体验冬季旅行的独特魅力
        </p>

        <div className="flex flex-wrap justify-center gap-8 text-sm text-muted-foreground">
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 rounded-full bg-primary"></div>
            <span>精选景点推荐</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 rounded-full bg-accent"></div>
            <span>专业路书指导</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-2 h-2 rounded-full bg-primary"></div>
            <span>智能行程规划</span>
          </div>
        </div>
      </div>
    </motion.div>
  );
};

import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

interface Snowflake {
  id: number;
  x: number;
  delay: number;
  duration: number;
  size: number;
}

export const Snowfall = () => {
  const [snowflakes, setSnowflakes] = useState<Snowflake[]>([]);

  useEffect(() => {
    const flakes: Snowflake[] = Array.from({ length: 30 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      delay: Math.random() * 5,
      duration: 10 + Math.random() * 10,
      size: 2 + Math.random() * 4,
    }));
    setSnowflakes(flakes);
  }, []);

  return (
    <div className="fixed inset-0 pointer-events-none z-0 overflow-hidden">
      {snowflakes.map((flake) => (
        <motion.div
          key={flake.id}
          className="absolute bg-snow-white rounded-full opacity-70"
          style={{
            left: `${flake.x}%`,
            width: flake.size,
            height: flake.size,
            top: '-10px',
          }}
          animate={{
            y: ['0vh', '110vh'],
            x: [0, Math.sin(flake.id) * 50, 0],
          }}
          transition={{
            duration: flake.duration,
            delay: flake.delay,
            repeat: Infinity,
            ease: 'linear',
          }}
        />
      ))}
    </div>
  );
};

